var SlothLogger = require('./lib/index.js');


var logger = new SlothLogger.Logger({         
    emailSettings: {   
        from: 'noreply@seas.harvard.edu',
        to: 'nater@seas.harvard.edu',
        transportConfig: {
            host: 'smtp-outbound.seas.harvard.edu',
            port: 25,
            secure: false,
            ignoreTLS: true
        }
    },
    levels: {
        error: { color: 'blue' }
    }
});

var aggregator = new SlothLogger.Aggregator();
//logger.setAggregator('error', aggregator);
//logger.setAggregator('info', aggregator);

logger.setLogColor('info', 'green');
logger.setLogFile('error', 'logs/error.log');
logger.sendEmail('error', true);

logger.error('error line 1');
logger.error('error line 2');
logger.setLogColor('error', 'red');
logger.error('error line 3');

logger.warn('warn line');
logger.info('info line');
logger.inspect('inspect line');
logger.debug('debug line');

var obj = { 'a': '1', 'b': '2', 'c': [1, 2, 3], 'd': { 'a': [1, 3, 4, 5, 6] }, 'a2': aggregator  };
obj.self = obj;
var txt = 'Text';


logger.inspect('msg', txt, obj);

aggregator.send();

